    @Autowired
    AdDeployNoticeImpl adDeployNoticeImpl;

    @RequestMapping(value = "/")
    public String none() {
        Random ran = new Random();
        int i = ran.nextInt(welcome.length);
        return "<p style=\"font-family: serif;color: grey;font-weight: 600;width:100%;text-align:center;\">" + welcome[i].replace(" ", ".") + "</p>";
    }

    @RequestMapping(value = "/hi")
    public String hi() {
        return "<p style=\"width:100%;text-align:center\">" + hi.replace(" ", "`") + "</p>";
    }

    @RequestMapping(value = "/hello")
    public String hello() {
        return "<p style=\"width:100%;text-align:center\">" + hello.replace(" ", "`") + "</p>";
    }

    @RequestMapping(value = "/outOfRole", produces = "application/json")
    public String outOfRole(HttpServletResponse response) {
        response.setStatus(401);
        Pojo poj = new CommonPojo();
        poj.setRetMessage("对不起，您暂时没有权限访问！");
        poj.setRetCode("");
        return JsonpUtil.modelToJson(poj);
    }

    @RequestMapping(value = "/getTime")
    public String getTime() {
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy年MM月dd日 HH时mm分ss秒");
        return sdf.format(new Date());
    }

    @RequestMapping(value = "/testErrorUrl", produces = "application/json")
    public String testErrorUrl(HttpServletRequest request, HttpServletResponse response) throws Exception {
        System.out.println("进入测试错误方法！");
        throw new Exception("测试页面异常");
    }

    @RequestMapping(value = "/qryNotice", produces = "application/json")
    public List<AdDeployNotice> qryNotice(@RequestParam long noticeId) {
        List<AdDeployNotice> adDeployNoticeList = adDeployNoticeImpl.qryAllDeployNotice();
        if (noticeId != 0 && !adDeployNoticeList.isEmpty()) {
            List<AdDeployNotice> adDeployNoticeList1 = new ArrayList<>();
            adDeployNoticeList1.add(adDeployNoticeList.get(0));
            return adDeployNoticeList1;
        } else {
            return adDeployNoticeList;
        }
    }
